import { apiKey } from './config';





const Yelp = {
    // The search method takes term, location, and sortBy as its parameters
    async search(term, location, sortBy) {
      // Build the URL using interpolation to include the term, location, and sortBy variables
      const url = `https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}&sort_by=${sortBy}`;

      try {
        // Make a fetch request to the Yelp API with the constructed URL and the API key in the headers
        const response = await fetch(url, {
          headers: {
            Authorization: `Bearer ${apiKey}`,
          },
        });

        // If the response is ok (HTTP status code is 200), proceed to process the response
        if (response.ok) {
          // Convert the response to a JSON object
          const jsonResponse = await response.json();
          // Check if the jsonResponse has a businesses property (an array of businesses)
          if (jsonResponse.businesses) {
            // Map the jsonResponse.businesses array to a new array with only the required properties
            return jsonResponse.businesses.map((business) => ({
              id: business.id,
              imageSrc: business.image_url,
              name: business.name,
              address: business.location.address1,
              city: business.location.city,
              state: business.location.state,
              zipCode: business.location.zip_code,
              category: business.categories[0].title,
              rating: business.rating,
              reviewCount: business.review_count,
            }));
          }
        }
        // If the response is not ok, throw an error
        throw new Error('Request failed');
      } catch (error) {
        // Log the error if something goes wrong during the fetch or processing
        console.log(error);
      }
    },
  };

  export default Yelp;
